import React, { useState } from 'react';

function TextGenerator() {
  const [seedText, setSeedText] = useState('');
  const [numWords, setNumWords] = useState(50);
  const [temperature, setTemperature] = useState(1.0);
  const [generatedText, setGeneratedText] = useState('');

  const handleGenerate = async () => {
    try {
      const response = await fetch('http://localhost:8000/generate-text', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          seed_text: seedText, // Ensure keys match the backend model
          num_words: numWords,
          temperature: temperature,
        }),
      });

      const data = await response.json();
      console.log('Generated text response:', data.generated_text);
      setGeneratedText(data.generated_text);
    } catch (error) {
      console.error('Error generating text:', error);
    }
  };

  return (
    <div>
      <h2>Text Generator</h2>
      <textarea
        value={seedText}
        onChange={(e) => setSeedText(e.target.value)}
        placeholder="Enter seed text"
      />
      <input
        type="number"
        value={numWords}
        onChange={(e) => setNumWords(Number(e.target.value))}
        placeholder="Number of words"
      />
      <input
        type="number"
        value={temperature}
        onChange={(e) => setTemperature(Number(e.target.value))}
        placeholder="Temperature"
        step="0.1"
        min="0.1"
        max="2.0"
      />
      <button onClick={handleGenerate}>Generate Text</button>
      {generatedText && <pre>{generatedText}</pre>}
    </div>
  );
}

export default TextGenerator;
