import React, { useState } from 'react';

function TextGenerator() {
  const [seedText, setSeedText] = useState('');
  const [numWords, setNumWords] = useState(50);
  const [temperature, setTemperature] = useState(1.0);
  const [generatedText, setGeneratedText] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleGenerate = async () => {
    if (!seedText.trim()) {
      setError('Please enter some seed text');
      return;
    }

    setLoading(true);
    setError('');
    try {
      const response = await fetch('http://localhost:8000/generate-text', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          seed_text: seedText,
          num_words: numWords,
          temperature: temperature,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to generate text');
      }

      const data = await response.json();
      setGeneratedText(data.generated_text);
      setError('');
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
      console.error('Error generating text:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setSeedText('');
    setGeneratedText('');
    setError('');
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(generatedText);
    alert('Text copied to clipboard!');
  };

  const getTemperatureDescription = (temp: number) => {
    if (temp <= 0.5) return 'Conservative - More predictable';
    if (temp <= 1.0) return 'Balanced - Mix of predictability & creativity';
    if (temp <= 1.5) return 'Creative - More varied output';
    return 'Wild - Maximum randomness';
  };

  return (
    <div className="max-w-2xl mx-auto">
      {/* Form Card */}
      <div className="bg-white rounded-2xl shadow-2xl p-8 mb-8">
        <h2 className="text-3xl font-bold text-gray-800 mb-8 flex items-center gap-3">
          <span className="bg-gradient-to-r from-indigo-600 to-purple-600 text-white w-10 h-10 rounded-full flex items-center justify-center text-lg">
            ✨
          </span>
          Generate New Text
        </h2>

        {/* Error Message */}
        {error && (
          <div className="mb-6 p-4 bg-red-50 border-l-4 border-red-500 rounded-lg">
            <p className="text-red-700 font-medium">⚠️ {error}</p>
          </div>
        )}

        {/* Seed Text Section */}
        <div className="mb-8">
          <label className="block text-sm font-semibold text-gray-700 mb-3 flex items-center gap-2">
            <span className="text-indigo-600">📝</span>
            Seed Text
            <span className="text-red-500">*</span>
          </label>
          <textarea
            value={seedText}
            onChange={(e) => setSeedText(e.target.value)}
            placeholder="Enter some starting text to generate from... (e.g., 'The future of AI')"
            disabled={loading}
            className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:outline-none focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 transition-all disabled:bg-gray-100 disabled:cursor-not-allowed resize-none font-medium"
            rows={5}
          />
          <p className="text-xs text-gray-500 mt-2">
            💡 The longer and more descriptive your seed text, the better the generated output
          </p>
        </div>

        {/* Controls Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
          {/* Word Count */}
          <div className="bg-gradient-to-br from-blue-50 to-indigo-50 rounded-xl p-6">
            <label className="block text-sm font-semibold text-gray-700 mb-3 flex items-center gap-2">
              <span className="text-lg">📊</span>
              Number of Words
            </label>
            <div className="flex items-center gap-4">
              <input
                type="range"
                min="10"
                max="200"
                value={numWords}
                onChange={(e) => setNumWords(Number(e.target.value))}
                disabled={loading}
                className="flex-1 h-2 bg-gradient-to-r from-blue-300 to-indigo-400 rounded-lg appearance-none cursor-pointer disabled:cursor-not-allowed disabled:opacity-50"
                style={{
                  background: `linear-gradient(to right, #93c5fd 0%, #6366f1 ${(numWords / 200) * 100}%, #e5e7eb ${(numWords / 200) * 100}%, #e5e7eb 100%)`,
                }}
              />
              <div className="bg-white border-2 border-indigo-300 rounded-lg px-4 py-2 min-w-16 text-center font-bold text-indigo-600">
                {numWords}
              </div>
            </div>
            <p className="text-xs text-gray-600 mt-2">Range: 10 - 200 words</p>
          </div>

          {/* Temperature */}
          <div className="bg-gradient-to-br from-purple-50 to-pink-50 rounded-xl p-6">
            <label className="block text-sm font-semibold text-gray-700 mb-3 flex items-center gap-2">
              <span className="text-lg">🔥</span>
              Temperature (Creativity)
            </label>
            <div className="flex items-center gap-4">
              <input
                type="range"
                min="0.1"
                max="2.0"
                step="0.1"
                value={temperature}
                onChange={(e) => setTemperature(Number(e.target.value))}
                disabled={loading}
                className="flex-1 h-2 bg-gradient-to-r from-purple-300 to-pink-400 rounded-lg appearance-none cursor-pointer disabled:cursor-not-allowed disabled:opacity-50"
                style={{
                  background: `linear-gradient(to right, #e9d5ff 0%, #f472b6 ${((temperature - 0.1) / 1.9) * 100}%, #e5e7eb ${((temperature - 0.1) / 1.9) * 100}%, #e5e7eb 100%)`,
                }}
              />
              <div className="bg-white border-2 border-purple-300 rounded-lg px-4 py-2 min-w-16 text-center font-bold text-purple-600">
                {temperature.toFixed(1)}
              </div>
            </div>
            <p className="text-xs text-gray-600 mt-2">
              {getTemperatureDescription(temperature)}
            </p>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex gap-4 justify-end">
          <button
            onClick={handleClear}
            disabled={loading || !seedText}
            className="px-6 py-3 border-2 border-gray-300 text-gray-700 font-semibold rounded-lg hover:bg-gray-50 transition-all disabled:opacity-50 disabled:cursor-not-allowed hover:shadow-md"
          >
            ✕ Clear
          </button>
          <button
            onClick={handleGenerate}
            disabled={loading || !seedText.trim()}
            className="px-8 py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-semibold rounded-lg hover:shadow-lg transition-all disabled:bg-gray-400 disabled:cursor-not-allowed disabled:shadow-none flex items-center gap-2 hover:scale-105 active:scale-95"
          >
            {loading ? (
              <>
                <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                Generating...
              </>
            ) : (
              <>
                <span>✨</span>
                Generate Text
              </>
            )}
          </button>
        </div>
      </div>

      {/* Output Section */}
      {generatedText && (
        <div className="bg-white rounded-2xl shadow-2xl p-8 animate-in fade-in duration-300">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
              <span className="text-2xl">🎯</span>
              Generated Text
            </h3>
            <span className="text-xs bg-green-100 text-green-700 px-3 py-1 rounded-full font-semibold">
              ✓ Complete
            </span>
          </div>

          <div className="bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50 rounded-xl p-8 border-2 border-indigo-200 mb-6">
            <p className="text-gray-800 leading-relaxed whitespace-pre-wrap text-base font-medium">
              {generatedText}
            </p>
          </div>

          <div className="flex gap-3">
            <button
              onClick={handleCopy}
              className="px-4 py-3 bg-indigo-100 text-indigo-700 font-semibold rounded-lg hover:bg-indigo-200 transition-all flex items-center gap-2 hover:shadow-md"
            >
              📋 Copy to Clipboard
            </button>
            <button
              onClick={() => setGeneratedText('')}
              className="px-4 py-3 bg-gray-100 text-gray-700 font-semibold rounded-lg hover:bg-gray-200 transition-all flex items-center gap-2 hover:shadow-md"
            >
              🔄 Generate Again
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default TextGenerator;
